const input = $input.all()[0].json;

// Handle different input structures - plotContext might be nested or at root
let plotContext = input.plotContext || input.body?.plotContext || input;
let voiceId = input.voiceId || input.body?.voiceId;

// Debug log
console.log('Input keys:', Object.keys(input));
console.log('plotContext keys:', plotContext ? Object.keys(plotContext) : 'null');
console.log('voiceId:', voiceId);

const items = [];

// 1. Premise
if (plotContext && plotContext.premise) {
  items.push({
    json: {
      type: 'premise',
      text: plotContext.premise,
      voiceId: voiceId
    }
  });
}

// 2. Investigators
if (plotContext && plotContext.investigatorThreads) {
  for (const thread of plotContext.investigatorThreads) {
    const text = (thread.personalStakes || '') + ' ' + (thread.connectionToThreat || '');
    if (text.trim()) {
      items.push({
        json: {
          type: 'investigator',
          id: thread.playerId,
          text: text,
          voiceId: voiceId
        }
      });
    }
  }
}

// 3. Music (Only one track based on themes)
if (plotContext && plotContext.activeThemes && plotContext.activeThemes.length > 0) {
  // Take just a few keywords, not full sentences
  const themeKeywords = plotContext.activeThemes.slice(0, 3).map(t => t.split('â€”')[0].trim()).join(', ');
  items.push({
    json: {
      type: 'music',
      text: `Dark eldritch horror ambient music, slow, ominous, ${themeKeywords}`,
      voiceId: null
    }
  });
}

// Fallback if no items generated
if (items.length === 0) {
  return [{ json: { error: 'No valid plot context found', inputKeys: Object.keys(input) } }];
}

return items;

const results = {
  premise: null,
  investigatorStakes: {},
  backgroundMusic: null
};

for (const item of $input.all()) {
  const type = item.json.type;
  const binaryKey = Object.keys(item.binary || {})[0];
  if (!binaryKey) continue;

  const base64Data = 'data:' + item.binary[binaryKey].mimeType + ';base64,' + item.binary[binaryKey].data;

  if (type === 'music') {
    results.backgroundMusic = base64Data;
  } else if (type === 'premise') {
    results.premise = base64Data;
  } else if (type === 'investigator') {
    results.investigatorStakes[item.json.id] = base64Data;
  }
}

return [{ json: results }];
