parameters.jsCode:
// Parse the AI response and harden output against markdown / extra text.
const raw = $input.first().json.output || $input.first().json.text || $input.first().json.message?.content || $input.first().json;

let parsed = raw;

if (typeof raw === 'string') {
  // Extract JSON from markdown code fences if present
  const m = raw.match(/```(?:json)?\s*([\s\S]*?)```/i);
  const jsonStr = (m ? m[1] : raw).trim();
  try {
    parsed = JSON.parse(jsonStr);
  } catch (e) {
    throw new Error('Failed to parse AI response as JSON');
  }
}

if (!parsed || typeof parsed !== 'object') {
  throw new Error('AI response is not an object');
}

// Basic structural validation
const encounter = parsed.encounter;
const nodes = parsed.nodes;
if (!encounter || typeof encounter !== 'object') throw new Error('Missing encounter object');
if (!Array.isArray(nodes)) throw new Error('Missing nodes array');
if (!encounter.startingNodeId) throw new Error('Missing encounter.startingNodeId');

const nodeIds = new Set(nodes.map(n => n.id));
if (!nodeIds.has(encounter.startingNodeId)) throw new Error('startingNodeId does not exist in nodes');

for (const n of nodes) {
  if (!n || typeof n !== 'object') throw new Error('Invalid node');
  if (!n.id || !n.type || !n.text) throw new Error('Node missing id/type/text');

  if (n.type === 'decision') {
    if (!Array.isArray(n.choices) || n.choices.length === 0) throw new Error(`Decision node ${n.id} missing choices`);
    for (const c of n.choices) {
      if (!c.nextNodeId || !nodeIds.has(c.nextNodeId)) throw new Error(`Choice in ${n.id} points to missing node`);
    }
  }

  if (n.type === 'test') {
    if (!n.test) throw new Error(`Test node ${n.id} missing test`);
    if (!nodeIds.has(n.test.passNodeId) || !nodeIds.has(n.test.failNodeId)) throw new Error(`Test node ${n.id} points to missing pass/fail node`);
  }
}

// Ensure encounter.narrative exists
if (!encounter.narrative) {
  const startNode = nodes.find(n => n.id === encounter.startingNodeId);
  encounter.narrative = startNode?.text || '';
}

return { json: parsed };

parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "encounter": {
      "type": "object",
      "properties": {
        "title": { "type": "string", "description": "Short evocative title" },
        "narrative": { "type": "string", "description": "2-3 sentence scene setup shown first" },
        "flavorText": { "type": "string", "description": "Optional atmospheric quote" },
        "startingNodeId": { "type": "string", "description": "Must match a node id" }
      },
      "required": ["title", "narrative", "startingNodeId"]
    },
    "nodes": {
      "type": "array",
      "description": "All nodes in the encounter tree",
      "items": {
        "type": "object",
        "properties": {
          "id": { "type": "string" },
          "text": { "type": "string", "description": "2-4 sentence narrative" },
          "type": { "type": "string", "enum": ["decision", "test", "outcome"] },
          "choices": {
            "type": "array",
            "description": "For decision nodes only",
            "items": {
              "type": "object",
              "properties": {
                "id": { "type": "string" },
                "label": { "type": "string" },
                "description": { "type": "string" },
                "nextNodeId": { "type": "string" }
              },
              "required": ["id", "label", "nextNodeId"]
            }
          },
          "test": {
            "type": "object",
            "description": "For test nodes only",
            "properties": {
              "skill": { "type": "string", "enum": ["Lore", "Influence", "Observation", "Strength", "Will"] },
              "difficulty": { "type": "number", "minimum": 0, "maximum": 3 },
              "passNodeId": { "type": "string" },
              "failNodeId": { "type": "string" }
            },
            "required": ["skill", "difficulty", "passNodeId", "failNodeId"]
          },
          "effects": {
            "type": "object",
            "description": "For outcome nodes - mechanical effects",
            "properties": {
              "healthChange": { "type": "number" },
              "sanityChange": { "type": "number" },
              "cluesGained": { "type": "number" },
              "doomChange": { "type": "number" },
              "conditionsGained": { "type": "array", "items": { "type": "string" } },
              "conditionsRemoved": { "type": "array", "items": { "type": "string" } },
              "assetsGained": { "type": "array", "items": { "type": "string" } },
              "assetsLost": { "type": "array", "items": { "type": "string" } }
            }
          }
        },
        "required": ["id", "text", "type"]
      }
    },
    "tensionChange": { "type": "number", "description": "How much to change game tension (-2 to +2)" },
    "newPlotPoints": { "type": "array", "items": { "type": "string" }, "description": "New story developments" }
  },
  "required": ["encounter", "nodes"]
}

