          "Set": {
            "text": "01Core",
            "links": [
              {
                "text": "Core",
                "href": "/wiki/Eldritch_Horror"
              }
            ]
          },
          "Encounter": {
            "text": "You scrape away year of moss and lichen from the stone altar, uncovering a series of prehistoric symbols. You think you can interpret the carvings (). If you pass, gain 1 Clue or 1 Spell.",
            "links": [
              {
                "text": "1 Clue",
                "href": "/wiki/Clues"
              },
              {
                "text": "Spell",
                "href": "/wiki/Spell"
              }
            ],
            "images": [
              {
                "alt": "Lore",
                "src": "data:image/gif;base64,R0lGODlhAQABAIABAAAAAP///yH5BAEAAAEALAAAAAABAAEAQAICTAEAOw%3D%3D"
              },
              {
                "alt": "Clue",
                "src": "data:image/gif;base64,R0lGODlhAQABAIABAAAAAP///yH5BAEAAAEALAAAAAABAAEAQAICTAEAOw%3D%3D"
              }
